"use strict";

var mongoose = require('mongoose');
var userSchema = new mongoose.Schema({
  name: {
    type: String,
    required: true,
    trim: true
  },
  email: {
    type: String,
    required: true,
    unique: true
  },
  password: {
    type: String,
    required: true
  },
  role: {
    type: Number,
    "default": 0
  },
  cart: {
    type: Array,
    "default": []
  }
}, {
  timestamps: true
});
module.exports = mongoose.model('Users', userSchema);
//# sourceMappingURL=data:application/json;charset=utf-8;base64,

